import React from "react"
import './GroupButton.css'
import styled from 'styled-components';

export default function GroupButton(){
    const types = ['Continent', 'Language']
    const [active, setActive] = React.useState(types[0]); 
    const [selected, setSelected] = React.useState({
        continent: false,
        language: false,
    });

    const TurnButton = styled.button`
        width:200px;
        height:30px;
        margin-left:100px;
        cursor:pointer;
        background: -moz-linear-gradient(top, rgb(219, 219, 219) 0%, rgb(219, 219, 219) 84%, #6d6d6d 100%);
        background: -webkit-linear-gradient(top, rgb(219, 219, 219) 0%, rgb(219, 219, 219) 84%, #6d6d6d 100%);
        background: linear-gradient(to bottom, rgb(219, 219, 219) 0%, rgb(219, 219, 219) 84%, #6d6d6d 100%);
        border-style: solid;
        ${({ active }) =>
            active &&
        `
        background: -moz-linear-gradient(top, rgb(219, 219, 219) 0%, rgb(219, 219, 219) 84%, #6d6d6d 100%);
        background: -webkit-linear-gradient(top, rgb(219, 219, 219) 0%, rgb(219, 219, 219) 84%, #6d6d6d 100%);
        background: linear-gradient(to bottom, rgb(38, 131, 253) 0%, rgb(38, 131, 253) 84%, #6d6d6d 100%);
        `}    
        `;

    const ContButton = () => {
        setSelected({
            continent: true,
            language: false,
        });
    }

    const LangButton = (e) => {
        setSelected({
            // Language:true,
            // Continent:false,
            ...selected,
            [e.target.name]: !selected.e.target.name
        })
    }

    return(
        <div className="groups">
            <h1>Group by:</h1>
            {/* <button type='button' className="buttons">
                Continent
            </button> */}
            <TurnButton name='Continent' onClick={() => ContButton()}>
                Continent
            </TurnButton>
            <TurnButton name='Language' onClick={() => LangButton()}>
                Language
            </TurnButton>
            {/* <button type='button' className="buttons">
                Language
            </button> */}
        </div>
    )
}
